document.addEventListener("alpine:init",()=>{window.Alpine.store("treeNodeSidebar",{isResizing:!1,startX:0,startWidth:0,init(){let i=document.querySelector(".tree-node-resizer"),e=document.querySelector(".tree-node-sidebar");if(!i||!e){console.error("Resizer or sidebar element not found");return}let s=t=>{this.isResizing=!0,this.startX=t.pageX,this.startWidth=parseInt(window.getComputedStyle(e).width,10),document.body.classList.add("resizing")},n=()=>{this.isResizing=!1,document.body.classList.remove("resizing")},r=t=>{if(!this.isResizing)return;let d=this.startWidth+(t.pageX-this.startX);e.style.width=`${Math.max(100,Math.min(d,window.innerWidth*.5))}px`};i.addEventListener("mousedown",s),document.addEventListener("mousemove",r),document.addEventListener("mouseup",n)}})});
